(2820, 269, '_section_5_content', 'field_5c4b4b88abd4f'),
(2821, 269, 'cta_headline_left', 'Why World Class Contracting?'),
(2822, 269, '_cta_headline_left', 'field_page_5bfef75ac4a05'),
(2823, 269, 'cta_content_left', 'Our project experience ranges from designing and installing mechanical systems including plumbing, gas-fittin, HVAC, Storm, and sprinklers, in estates, high rises, retail and industrial shops, processing plants, schools, hotels, restaurants, hospitals, and agricultural barns.'),
